import React, { useEffect } from 'react';
import { capitalizeFirstLetter } from '../../utils/helpers';
import avatar from '../../assets/images/sunnyPic/sunny6.jpeg';
import { Link } from 'react-router-dom';
import coverImage from '../../assets/1.jpeg';
function Nav(props) {
  const {
    aboutSelected,
    setAboutSelected,
    resumeSelected,
    setResumeSelected,
    portfolioSelected,
    setPortfolioSelected,
    contactSelected,
    setContactSelected,
  } = props;
  return (
    <header className="container-fluid my-4 h-3 " 
    style={{backgroundImage:`url(${coverImage})`}} 
    >
      <div className="row">
      <div className="col-12 my-4 my-md-0 col-md-6 col-lg-5 ">
     <Link to="/" className='text-decoration-none ' data-testid="link" href="/">
      <h1 className="text-white" style={{marginLeft:"160px",fontSize:"60px", fontFamily: 'Poiret One'}}>
          Satwinder
      </h1>
      </Link>
      </div>
      <nav className='col-12 col-md-6 col-lg-7 my-3'>
        <ul className='row list-unstyled py-2' style={{fontSize:"1.5rem"}}>
          <li className="col-3 col-md-3 col-lg-3 ">
            <Link to = "/about" className="text-decoration-none text-white"
            data-testid="about" href="#about" >
            {/* // onClick={() => {
              setResumeSelected(false);
              setContactSelected(false);
              setPortfolioSelected(false);
              setAboutSelected(true);
              }}> */}
              About
            </Link>
          </li>
          <li className="col-3 col-md-3 col-lg-3">
            <Link to ="/portfolio" className="text-decoration-none text-white"
            href="#portfolio">
             {/* onClick={() => {
              setResumeSelected(false);
              setContactSelected(false);
              setPortfolioSelected(true);
              setAboutSelected(false);
              }}> */}
              Portfolio
            </Link>
          </li>
          <li className="col-3 col-md-3 col-lg-3">
            <Link to = "/contact" className="text-decoration-none text-white"
             href="#contact">
            {/* onClick={() => {
              setResumeSelected(false);
              setContactSelected(true);
              setPortfolioSelected(false);
              setAboutSelected(false);
            }
            } > */}
               Contact
            </Link>
          </li>
          <li className="col-3 col-md-3 col-lg-3">
            <Link to="resume" 
            className="text-decoration-none text-white"
             href="#resume">
             {/* onClick={() => {
              setResumeSelected(false);
              setResumeSelected(true);
              setContactSelected(false);
              setAboutSelected(false);
              }} > */}
              Resume
              </Link>
          </li>
        </ul>
      </nav>
      <div className='border border-dark rounded-circle m-sm-4m-md-3 position-absolute' 
      style={{top:"-12px",width:"150px" , height:"150px",backgroundImage:`url(${avatar})`,backgroundSize: "100% 100%"}}>
      </div>
      </div>
    </header>
  );
}

export default Nav;
