import React, { useEffect } from 'react';
import { capitalizeFirstLetter } from '../../utils/helpers';
import avatar from '../../assets/satwinder.png';
import { Link } from 'react-router-dom';
import coverImage from '../../assets/1.jpeg';
function Nav(props) {
  const {
    aboutSelected,
    setAboutSelected,
    resumeSelected,
    setResumeSelected,
    portfolioSelected,
    setPortfolioSelected,
    contactSelected,
    setContactSelected,
  } = props;
  return (
    <header className="row my-4 h-3 bg-secondary" style={{backgroundImage:`url(${coverImage})`}} >
     <Link to="/" className='text-decoration-none col-md-12 col-lg-5 ' data-testid="link" href="/">
      <h1 className="text-white" style={{marginLeft:"160px",fontSize:"60px", fontFamily: 'Poiret One'}}>
          Satwinder
      </h1>
      </Link>
      <nav className='col-lg-7 my-3'>
        <ul className='row list-unstyled py-2' style={{fontSize:"1.5rem"}}>
          <li className="col-sm-3 ">
            <Link to = "/about" className={aboutSelected?'text-decoration-none text-primary':'text-decoration-none'} 
            data-testid="about" href="#about" >
            {/* // onClick={() => {
              setResumeSelected(false);
              setContactSelected(false);
              setPortfolioSelected(false);
              setAboutSelected(true);
              }}> */}
              About me
            </Link>
          </li>
          <li className="col-sm-3">
            <Link to ="/portfolio" className={portfolioSelected ? 'text-decoration-none text-primary':'text-decoration-none'} 
            href="#portfolio">
             {/* onClick={() => {
              setResumeSelected(false);
              setContactSelected(false);
              setPortfolioSelected(true);
              setAboutSelected(false);
              }}> */}
              Portfolio
            </Link>
          </li>
          <li className="col-sm-3">
            <Link to = "/contact" className={contactSelected ? 'text-decoration-none text-primary':'text-decoration-none'}
             href="#contact">
            {/* onClick={() => {
              setResumeSelected(false);
              setContactSelected(true);
              setPortfolioSelected(false);
              setAboutSelected(false);
            }
            } > */}
               Contact
            </Link>
          </li>
          <li className="col-sm-3">
            <Link to="resume" 
            className={resumeSelected ? 'text-decoration-none text-primary':'text-decoration-none'}
             href="#resume">
             {/* onClick={() => {
              setResumeSelected(false);
              setResumeSelected(true);
              setContactSelected(false);
              setAboutSelected(false);
              }} > */}
              Resume
              </Link>
          </li>
        </ul>
      </nav>
      <div className='border border-dark rounded-circle m-3 position-absolute' 
      style={{top:"-12px",width:"150px" , height:"150px",backgroundImage:`url(${avatar})`,backgroundSize: "100% 100%"}}>
      </div>
    </header>
  );
}

export default Nav;
