import React, { useState } from 'react';
import bookaround from '../../assets/images/bookaround.png';
import music from '../../assets/images/music.png';
import note from '../../assets/images/note.png';
import blog from '../../assets/images/blog.png';
import weather from '../../assets/images/weather.png';
import workday from '../../assets/images/workday.png';
import { capitalizeFirstLetter } from '../../utils/helpers';

function Project(props) {
  const [isShown, setIsShown] = useState(false);
  const [hide, setHide] = useState('0px');
 const apps = [
  {
  title:'Book Around',
  type:'Full Stack',
  liveUrl:'https://floating-reef-77198.herokuapp.com/',
  gitUrl:'https://github.com/TheBookStore05/bookaround.git',
  img:'bookaround',
  },
  {
    title:'Music Slayers',
    type:'Full Stack',
    liveUrl:'https://floating-reef-77198.herokuapp.com/',
    gitUrl:'https://github.com/Project01-Team14/Music-Slayers.gits',
    img:'music',
  },
  {
    title:'Note taker',
    type:'Full Stack',
    liveUrl:'https://satwinder-note.herokuapp.com/',
    gitUrl:'https://github.com/satwinder191995/note-taker.git',
    img:'note',
  },
  {
    title:'Tech Blog',
    type:'My Sql',
    liveUrl:'https://project01-team14.github.io/Music-Slayers/',
    gitUrl:'https://satwinder-blog.herokuapp.com/',
    img:'blog',
  },
  {
    title:'Weather',
    type:'Frontend',
    liveUrl:'https://satwinder191995.github.io/weather/',
    gitUrl:'https://github.com/satwinder191995/weather.git',
    img:'weather',
  },
  {
    title:'Workday Scheduler',
    type:'Javascript',
    liveUrl:'https://satwinder191995.github.io/workdaySchedular/',
    gitUrl:'https://github.com/satwinder191995/workdaySchedular.git',
    img:'workday', 
  },
 ];
  // const { currentCategory } = props;
  return (
    <>
       <h1>{capitalizeFirstLetter("projects")}</h1>
       <section className='row'>
       { apps.map((app,i) =>(
        <a href={apps.liveUrl} className='col-5 m-4' key={i}>
        <article className =''
        key={i}
        style={{ height:"250px"
        // ,backgroundImage:`url(/images/${app.img}.png)` ,backgroundSize: "100% 100%",filter:"blur(4px)"
      }}
        // onMouseEnter={() => setIsShown(true)}
        // onMouseLeave={() => setIsShown(false)}
        onMouseEnter={(e) => {
          e.target.style.opacity="0.2"
          setIsShown(true)
          return(
            <h1 className='' style={{zIndex:"1"}}>hello</h1>
          )
        }}
        onMouseLeave={(e) => {
        e.target.style.opacity="1"
        setIsShown(false)
        }}
        >
            <img
            src={require(`../../assets/images/${app.img}.png`)}
            alt={app.title}
            // className="img-thumbnail mx-1"
            // onClick={() => toggleModal(image, i)}
            key={app.title}
            style={{width:"100%",height:"100%"}}
          />
          {isShown &&(
            <div className='position-relative' style={{bottom:"230px"}}>
        <div className="d-flex"><h2 className='text-dark'>{app.title}</h2><span>hello</span></div>
        <h3 className='text-dark'>{app.type}</h3>
        </div>
         )}
        </article>
        </a>
       )
       )}
    </section>
    </>
  );
}
export default Project;
